mapper.update:
UPDATE crm_order_manual_t
SET flow_flag = #{flow_flag}, error_msg =
#{error_msg},PlanInDate = #{planInDateStr}

	, WhCode = #{whCode}

WHERE
delete_flag = 0
AND flow_flag = 0
AND wp_order_id = #{wp_order_id}

UPDATE crm_order_manual_t
SET flow_flag = #{flow_flag}

	, error_msg = #{error_msg}

WHERE
delete_flag = 0
AND ((flow_flag >= 0 AND flow_flag <= ${flow_flag}) or (flow_flag > 0 and ${flow_flag} < 0))

	AND wp_order_id = (select wp_order_id from
	crm_order_manual_detail_t where so_id=#{so_id}

	AND ((so_id is not null and TRIM(so_id) != '') OR (dn_id is not null and TRIM(dn_id) != ''))

	)


	AND wp_order_id = (select wp_order_id from
	crm_order_manual_detail_t where dn_id=#{dn_id}

	AND ((so_id is not null and TRIM(so_id) != '') OR (dn_id is not null and TRIM(dn_id) != ''))

	)


	AND wp_order_id = #{wp_order_id}

UPDATE crm_order_t
rrs_out_time = str_to_date(#{rrs_out_time}, '%Y-%m-%d %H:%i:%s')
wa_in_time = str_to_date(#{wa_in_time}, '%Y-%m-%d %H:%i:%s')
rrs_in_time = str_to_date(#{rrs_in_time}, '%Y-%m-%d %H:%i:%s')
,wa_in_num = #{wa_in_num}
where
so_id = #{so_id}
dn_id = #{dn_id}

UPDATE crm_order_manual_detail_t
SET po_id = #{po_id},
so_id= #{so_id},
dn_id=#{dn_id}
WHERE
wp_order_id = #{wp_order_id} and materials_id = #{materials_id}

mapper.sql:
FROM crm_order_manual_t AS m force index(ix_crm_order_manual_t_flag1), crm_order_manual_detail_t AS d LEFT JOIN crm_order_t crm ON d.so_id = crm.so_id AND crm.so_id IS NOT NULL AND TRIM(crm.so_id) != '' WHERE d.wp_order_id = m.wp_order_id and m.delete_flag = 0
and crm.bill_time >= #{planInDate_start}
and crm.bill_time < #{planInDate_end}
AND UPPER(d.so_id) LIKE CONCAT(CONCAT('%',UPPER (#{so_id})),'%')
AND UPPER(d.dn_id) LIKE CONCAT(CONCAT('%',UPPER (#{dn_id})),'%')
AND d.ed_channel_id in
#{item}
AND d.product_group_id in
#{item}
AND m.BillType = #{bill_type}
AND UPPER(d.wp_order_id) LIKE CONCAT(CONCAT('%',UPPER (#{order_id})),'%')
AND UPPER(m.CorpCode) LIKE CONCAT(CONCAT('%',UPPER (#{corpCode})),'%')
AND m.flow_flag in
#{item}
AND UPPER(d.materials_id) LIKE CONCAT(CONCAT('%',UPPER (#{materials_id})),'%')
AND UPPER(m.estorge_id) LIKE CONCAT(CONCAT('%',UPPER (#{whCode})),'%')
AND d.brand_id = #{brand_id}
AND d.category_id in
#{item}
AND UPPER(d.materials_desc) LIKE CONCAT(CONCAT('%',UPPER (#{materials_desc})),'%')
AND d.wp_order_id in
#{item}
AND UPPER(m.source_order_id) LIKE CONCAT(CONCAT('%',UPPER (#{source_order_id})),'%')
GROUP BY d.wp_order_id

mapper.select:
SELECT d.wp_order_id, d.po_id, d.materials_id, d.product_group_id, d.brand_id, d.InvState, d.Qty, d.UnitPrice, d.SumMoney, d.StockPrice, d.RetailPrice, d.BateRate, d.BateMoney, d.ProLossMoney, d.LossRate, d.IsFL, d.IsKPO, m.flow_flag, d.materials_desc, d.ed_channel_id, d.category_id, d.ActPrice, m.source_order_id, m.CorpCode, m.RegionId, m.BillType, m.les_regionId, m.stock_type, m.DestCenter, m.SysFlag, m.ReleBillCode, m.Maker, m.SaleOrgCode, m.BudgetOrg_name, m.BudgetOrg, m.BudgetSort, m.wp_order_id, CASE m.PlanInDate WHEN DEFAULT(m.PlanInDate) THEN '' ELSE date_format(m.PlanInDate,'%Y-%m-%d') END as planindate_display, m.ProDputy, m.PorMgr, m.CustMgr, m.WhCode, m.DestCode, m.CustCode, m.sap_flow_num, m.budgetsort, m.estorge_id, m.estorge_name, m.corpName, m.industry_trade_desc, m.payment_name, m.esale_name, m.rrs_distribution_name, m.error_msg, d.so_id, d.dn_id, date_format(crm.bill_time,'%Y-%m-%d %H:%i:%S') as bill_time_display, date_format(crm.rrs_out_time,'%Y-%m-%d %H:%i:%S') as rrs_out_time_display, date_format(crm.wa_in_time,'%Y-%m-%d %H:%i:%S') as wa_in_time_display, crm.bill_order_id
ORDER BY d.wp_order_id
limit ${m},${n}
