mapper.select:
	select
		`id`,
		`row_id`,
		`material_code`,
		`material_description`,
		`material_type`,
		`industry`,
		`mtl_group_code`,
		`primary_uom`,
		`department`,
		`delete_flag`,
		`created`,
		`last_upd`,
		`kind_one`,
		`kind_add`,
		`pro_type`,
		`type_disc`,
		`is_viliage_product`,
		`mark_flag`,
		`pro_band`,
		`length_number`,
		`width_number`,
		`high_number`,
		`store_layer_number`,
		`gross_weight_number`,
		`weight_unit`,
		`is_crust_flag`,
		`rebate_rate`,
		`sale_char`,
		`created_by`,
		`product_list`,
		`status`,
		`is_auto_update`,
		`product_type`,
		`modifier`,
		`price`
	from `item_base`

			 `delete_flag`= #{deleteFlag}
			 `delete_flag`= 0
			and `id`= #{id}
			and `row_id`= #{rowId}
			and lcase(`material_code`) like CONCAT(CONCAT('%', #{materialCode}),'%')
			and `material_description` like CONCAT(CONCAT('%', #{materialDescription}),'%')
			and `material_type`= #{materialType}
			and `industry`= #{industry}
			and `mtl_group_code`= #{mtlGroupCode}
			and `primary_uom`= #{primaryUom}

			and department in 

           		#{department}


			and `created`= #{created}
			and `last_upd`= #{lastUpd}
			and `kind_one`= #{kindOne}
			and `kind_add`= #{kindAdd}
			and `pro_type`= #{proType}
			and `type_disc`= #{typeDisc}
			and `is_viliage_product`= #{isViliageProduct}
			and `mark_flag`= #{markFlag}
			and `pro_band`= #{proBand}
			and `length_number`= #{lengthNumber}
			and `width_number`= #{widthNumber}
			and `high_number`= #{highNumber}
			and `store_layer_number`= #{storeLayerNumber}
			and `gross_weight_number`= #{grossWeightNumber}
			and `weight_unit`= #{weightUnit}
			and `is_crust_flag`= #{isCrustFlag}
			and `rebate_rate`= #{rebateRate}
			and `sale_char`= #{saleChar}
			and `created_by`= #{createdBy}
			and `product_list`= #{productList}
			and `status`= #{status}
			and `is_auto_update`= #{isAutoUpdate}

				and `product_type` = #{productType} 

			and `modifier`= #{modifier}

	limit #{start},#{size}

	select count(`id`) 
	from `item_base`

			 `delete_flag`= #{deleteFlag}
			 `delete_flag`= 0
			and `id`= #{id}
			and `row_id`= #{rowId}
			and lcase(`material_code`) like CONCAT(CONCAT('%', #{materialCode}),'%')
			and `material_description` like CONCAT(CONCAT('%', #{materialDescription}),'%')
			and `material_type`= #{materialType}
			and `industry`= #{industry}
			and `mtl_group_code`= #{mtlGroupCode}
			and `primary_uom`= #{primaryUom}

			and department in 

           		#{department}


			and `created`= #{created}
			and `last_upd`= #{lastUpd}
			and `kind_one`= #{kindOne}
			and `kind_add`= #{kindAdd}
			and `pro_type`= #{proType}
			and `type_disc`= #{typeDisc}
			and `is_viliage_product`= #{isViliageProduct}
			and `mark_flag`= #{markFlag}
			and `pro_band`= #{proBand}
			and `length_number`= #{lengthNumber}
			and `width_number`= #{widthNumber}
			and `high_number`= #{highNumber}
			and `store_layer_number`= #{storeLayerNumber}
			and `gross_weight_number`= #{grossWeightNumber}
			and `weight_unit`= #{weightUnit}
			and `is_crust_flag`= #{isCrustFlag}
			and `rebate_rate`= #{rebateRate}
			and `sale_char`= #{saleChar}
			and `created_by`= #{createdBy}
			and `product_list`= #{productList}
			and `status`= #{status}
			and `is_auto_update`= #{isAutoUpdate}

				and `product_type` = #{productType} 

			and `modifier`= #{modifier}

mapper.sql:
		FROM
		item_base itb 
    left join  `item_attribute` ita 
    on ita.value = itb.department
		WHERE
		1=1 and value_set_id = 'ProductGroup' 
		and (value!='00' and value != '01' and value != '20'and value != '30'and value != '99')

			AND material_type = #{type}


			AND department = #{product_group_id}


			AND status = #{status}


			AND is_auto_update = #{update}


			AND UPPER(material_code) LIKE CONCAT(CONCAT('%',UPPER
			(#{materials_id})),'%')


			AND UPPER(material_description) LIKE CONCAT(CONCAT('%',UPPER
			(#{materials_desc})),'%')


			AND cbs_category = #{category_id}
