mapper.delete:
DELETE
FROM sn_ods_tmfx_rebates_summary
WHERE 1=1

  and year = #{year,jdbcType=VARCHAR}


  and month = #{month,jdbcType=VARCHAR}


  and type = #{type,jdbcType=CHAR}

mapper.select:
select t.ecology_shop, t.year, t.month, t.type, sum(t.sale_amount)sale_amount, sum(t.base_rebates_amount)base_rebates_amount, sum(t.extra_rebates_amount)extra_rebates_amount from sn_ods_tmfx_rebates_summary t WHERE 1=1
and ECOLOGY_SHOP = #{ecologyShop,jdbcType=VARCHAR}
and YEAR = #{year,jdbcType=VARCHAR}
and MONTH = #{month,jdbcType=VARCHAR}
and TYPE = #{type,jdbcType=CHAR}
group by t.ecology_shop, t.year, t.month, t.type

