SELECT ID, ECOLOGY_SHOP, YEAR, MONTH, case TYPE when 'y' then '年度' when 'q' then '季度' when 'm' then '月度' end TYPE, INDUSTRY, BRAND, SALE_AMOUNT, REBATES_AMOUNT, TARGET, COMPLETION, INDUSTRY_TARGET, INDUSTRY_COMPLETION, BASE_POINT, EXTRA_REBATE, STEP_POINT, MARK_POINT, SKU_STEP_POINT, BASE_REBATES_AMOUNT, EXTRA_REBATES_AMOUNT, STEP_REBATES_AMOUNT, MARK_REBATES_AMOUNT, FIXED_AMOUNT, SKU_STEP_REBATES_AMOUNT, YX_COST, QT_COST, SHARE_PROPORTION, CASH_AMOUNT FROM sn_ods_tmfx_rebates_summary WHERE 1=1
and YEAR = #{year,jdbcType=VARCHAR}
and MONTH = #{month,jdbcType=VARCHAR}
and ECOLOGY_SHOP = #{ecologyShop,jdbcType=VARCHAR}
and INDUSTRY = #{industry,jdbcType=VARCHAR}

