select
nvl(sum(s.rebates_amount),0)rebates_amount
from sn_ods_tmfx_rebates_summary s
where 1=1

  and s.year = #{year}


  and s.month = #{month}


  and s.type = #{type}


  and s.brand=#{brand}


  and s.ECOLOGY_SHOP=#{ecologyShop}

select r.year, r.month, r.type, r.industry, r.brand, IFNULL(sum(r.rebates_amount),0)rebates_amount, IFNULL(sum(r.yx_cost),0)yx_cost, IFNULL(sum(r.sale_amount),0)sale_amount from sn_ods_tmfx_rebates_summary r WHERE 1=1
and r.year = #{year}
and r.month = #{month}
and r.type = #{type}
group by r.year, r.month, r.type, r.industry, r.brand

select r.year, r.month, r.ecology_shop, r.type, IFNULL(sum(r.sale_amount),0) sale_amount, IFNULL(sum(r.target),0) target, IFNULL(sum(r.qt_cost),0) qt_cost, IFNULL(sum(r.rebates_amount),0) rebates_amount from sn_ods_tmfx_rebates_summary r WHERE 1=1
and r.year = #{year}
and r.month = #{month}
and r.type = #{type}
group by r.year, r.month, r.ecology_shop, r.type

select
IFNULL(sum(r.sale_amount),0) sale_amount
